import React from 'react';
import './App.css';
import { Component } from 'react';
import { AR } from './AR'

export class MainPage extends Component{
    constructor(props){
        super(props);
        this.state = {
          showAR: false,
        };
    }
  
    handleShowAR = ()=>{
      this.setState({showAR: true});
    }
  
    render(){
      return(
      <div className="App">
        <header className="App-header">
          <button onClick={this.handleShowAR}>AR</button>
        </header>
        <body>
            <AR show={showAR}/>
        </body>
       </div>
      );
    }
  }
